import React, { useState, useEffect } from 'react';
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TextInput,
  TouchableOpacity,
  Image,
  Alert,
} from 'react-native';
import Icon from 'react-native-vector-icons/MaterialCommunityIcons';
import * as ImagePicker from 'expo-image-picker';
import axios from 'axios';

const CommunityScreen = () => {
  const [posts, setPosts] = useState([
    {
      id: '1',
      username: 'Tim Bakes',
      content: `Dear Student,You are special, and there are no limits to what you can achieve! ðŸ’«`,
      image: 'https://media.istockphoto.com/id/1297186591/photo/happy-laughing-smiling-caucasian-boy-student-by-yellow-bus-on-first-september-day-hard-of.jpg?s=612x612&w=0&k=20&c=hpp8iyeWC70zU2NgwPzjsLGF_F7pUDxlOYX_yL3IfAg=',
      likes: 357,
      userLiked: false,
      showComments: false,
      comments: [
        {
          id: 'c1',
          text: 'Hang in there, Tim!',
          username: 'Sarah Lee',
          userImage: 'data:image/jpeg;base64,',
          createdAt: new Date(),
        },
      ],
      userImage: 'data:image/jpeg;base64,',
      createdAt: new Date(),
    },
    {
      id: '2',
      username: 'Tim Bakes',
      content: `I'm having a great time `,
      image: 'data:image/jpeg;base64,',
      likes: 357,
      userLiked: false,
      showComments: false,
      comments: [
        {
          id: 'c1',
          text: 'Hang in there, Tim!',
          username: 'Sarah Lee',
          userImage: 'data:image/jpeg;base64,',
          createdAt: new Date(),
        },
      ],
      userImage: 'data:image/jpeg;base64,',
      createdAt: new Date(),
    },
  ]);

  const [newPost, setNewPost] = useState('');
  const [newComment, setNewComment] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    const requestPermission = async () => {
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== 'granted') {
        Alert.alert('Permission required', 'Permission to access media library is required!');
      }
    };
    requestPermission();
  }, []);

  const handleAddPost = async () => {
    if (newPost.trim() === '' && !selectedImage) {
      Alert.alert('Error', 'Post content or an image is required!');
      return;
    }

    let imageUrl = null;
    if (selectedImage) {
      try {
        imageUrl = await uploadImage(selectedImage);
      } catch (error) {
        console.error('Image upload failed:', error);
        Alert.alert('Error', 'Failed to upload image.');
        return;
      }
    }

    const newPostObj = {
      id: Math.random().toString(),
      username: 'New User',
      content: newPost,
      image: imageUrl,
      likes: 0,
      userLiked: false,
      showComments: false,
      comments: [],
      userImage: 'https://via.placeholder.com/50',
      createdAt: new Date(),
    };

    setPosts([newPostObj, ...posts]);
    setNewPost('');
    setSelectedImage(null);
  };

  const handleLike = (id) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) =>
        post.id === id
          ? {
            ...post,
            likes: post.userLiked ? post.likes - 1 : post.likes + 1,
            userLiked: !post.userLiked,
          }
          : post
      )
    );
  };

  const handleAddComment = (postId) => {
    if (newComment.trim() === '') {
      Alert.alert('Error', 'Comment cannot be empty!');
      return;
    }

    setPosts((prevPosts) =>
      prevPosts.map((post) =>
        post.id === postId
          ? {
            ...post,
            comments: [
              ...post.comments,
              {
                id: Math.random().toString(),
                text: newComment,
                username: 'New User',
                userImage: 'https://via.placeholder.com/50',
                createdAt: new Date(),
              },
            ],
          }
          : post
      )
    );
    setNewComment('');
  };

  const toggleComments = (id) => {
    setPosts((prevPosts) =>
      prevPosts.map((post) =>
        post.id === id
          ? { ...post, showComments: !post.showComments }
          : post
      )
    );
  };

  const pickImage = async () => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });

      if (!result.canceled) {
        setSelectedImage(result.assets[0].uri);
      }
    } catch (error) {
      console.error('Error picking image:', error);
    }
  };

  const uploadImage = async (imageUri) => {
    const formData = new FormData();
    formData.append('file', {
      uri: imageUri,
      name: 'image.jpg',
      type: 'image/jpeg',
    });

    try {
      const response = await axios.post('https://your-api-url.com/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      return response.data.url; // Assume the API returns the image URL.
    } catch (error) {
      throw new Error('Image upload failed');
    }
  };

  const formatDate = (date) => {
    const now = new Date();
    const diffInMs = now - date;
    const diffInMinutes = Math.floor(diffInMs / 60000);
    if (diffInMinutes < 60) return `${diffInMinutes} mins ago`;
    const diffInHours = Math.floor(diffInMinutes / 60);
    if (diffInHours < 24) return `${diffInHours} hours ago`;
    const diffInDays = Math.floor(diffInHours / 24);
    return `${diffInDays} days ago`;
  };

  const renderPost = ({ item }) => {
    const commentsToShow = item.showComments ? item.comments : item.comments.slice(0, 1);

    return (
      <View style={styles.postContainerWrapper}>
        <View style={styles.postContainer}>
          <View style={styles.postHeader}>
            <Image source={{ uri: item.userImage }} style={styles.userImage} />
            <View style={styles.userInfo}>
              <Text style={styles.username}>{item.username}</Text>
              <Text style={styles.postTime}>{formatDate(item.createdAt)}</Text>
            </View>
          </View>
          <Text style={styles.content}>{item.content}</Text>
          {item.image && <Image source={{ uri: item.image }} style={styles.postImage} />}
          <View style={styles.counts}>
            <Text style={styles.likeCount}>{item.likes} Likes</Text>
            <Text style={styles.commentCount}>{item.comments.length} Comments</Text>
          </View>
          <View
            style={[
              styles.postFooter,
              item.showComments
              // && styles.postFooterWithBottomBorder,
            ]}
          >
            <TouchableOpacity onPress={() => handleLike(item.id)} style={styles.iconButton}>
              <Icon
                name={item.userLiked ? 'heart' : 'heart-outline'}
                size={20}
                color={item.userLiked ? 'red' : '#0A505B'}
              />
              <Text style={styles.commentsHeader}>Likes</Text>
            </TouchableOpacity>
            <TouchableOpacity onPress={() => toggleComments(item.id)} style={styles.iconButton}>
              <Icon name="comment-text-outline" size={20} color="#0A505B" />
              <Text style={styles.commentsHeader}>Comments</Text>
            </TouchableOpacity>
          </View>

          <View style={styles.commentSection}>
            <FlatList
              data={commentsToShow}
              renderItem={({ item }) => (
                <View style={styles.commentCard}>
                  <View style={styles.commentHeader}>
                    <Image source={{ uri: item.userImage }} style={styles.commentUserImage} />
                    <View style={styles.commentUserInfo}>
                      <Text style={styles.commentUsername}>{item.username}</Text>
                      <Text style={styles.commentTime}>{formatDate(item.createdAt)}</Text>
                    </View>
                  </View>
                  <Text style={styles.commentText}>{item.text}</Text>
                </View>
              )}
              keyExtractor={(comment) => comment.id}
            />

            {item.comments.length > 1 && !item.showComments && (
              <TouchableOpacity onPress={() => toggleComments(item.id)} style={styles.showMoreButton}>
                {/* <Text style={styles.showMoreText}>Show all comments</Text> */}
              </TouchableOpacity>
            )}

            <View style={styles.newCommentContainer}>
              <TextInput
                style={styles.newCommentInput}
                placeholder="Write a comment..."
                value={newComment}
                onChangeText={setNewComment}
                multiline
              />
              <TouchableOpacity style={styles.commentButton} onPress={() => handleAddComment(item.id)}>
                <Text style={styles.commentButtonText}>Post</Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </View>
    );
  };

  return (
    <FlatList
      ListHeaderComponent={
        <>
          <View style={styles.headerlogo}>
            <Image source={require('../assets/images/logo.png')} style={styles.logo} />
            <Text style={styles.header}>Community</Text>
          </View>
          <View style={styles.newPostContainer}>
            <TextInput
              style={styles.newPostInput}
              placeholder="Write something..."
              value={newPost}
              onChangeText={setNewPost}
              multiline
            />
            <TouchableOpacity style={styles.imagePickerButton} onPress={pickImage}>
              <Icon name="image" size={30} color="#0A505B" />
            </TouchableOpacity>
            <TouchableOpacity style={styles.postButton} onPress={handleAddPost}>
              <Text style={styles.postButtonText}>Post</Text>
            </TouchableOpacity>
          </View>
        </>
      }
      data={posts}
      renderItem={renderPost}
      keyExtractor={(item) => item.id}
      contentContainerStyle={styles.container}
    />
  );
};

export default CommunityScreen;

